        string source_CNcode= "";
        string[] plaintext;
        string[] cncode;
        //因为中文电码数据量过大，只能先把电码和文字分别放入两个字符串中，然后由程序将其装入数组

        
        public ChineseCommercialCode()//构造函数（将字符串数据载入到数组以便程序搜索对应电码）
        {
            plaintext = new string[source_plaintext.Length];//初始化数组
            cncode = new string[source_CNcode.Length];
            for (int i = 0; i < source_plaintext.Length; i++)//装入数组
            {
                plaintext[i] = source_plaintext.Substring(i,1);
            }
            int a = 0;
            for (int i = 0; i < source_CNcode.Length; i=i+4)//装入数组
            {
                cncode[a] = source_CNcode.Substring(i,4);
                a++;
            }             
                     
        }

        /// <summary>
        /// 文字转中文电码
        /// </summary>
        /// <param name="inputtext">待转换的文字</param>
        /// <returns>电码</returns>
        public string TextToCode(string inputtext)//文字转中文电码
        {
            string OUTPUTTEXT = inputtext;//读取参数
            for (int i = 0; i < plaintext.Length; i++)
            {
                OUTPUTTEXT = OUTPUTTEXT.Replace(plaintext[i], cncode[i] + " ");//替换为对应电码
            }
            return OUTPUTTEXT;//输出
        }

        public string CodeToText(string inputtext)//中文电码转文字
        {
            string outputtext = inputtext;//读取参数
            string[] converting = outputtext.Split(' ');//装入数组

            for (int i = 0; i < converting.Length; i++)
            {
                for (int a = 0; a < cncode.Length; a++)
                {
                    if (converting[i] == cncode[a])
                    {
                        converting[i] = plaintext[a];//替换为中文
                    }
                }
            }

            outputtext = null;
            for (int i = 0; i < converting.Length; i++)
            {
                outputtext = outputtext + converting[i];//数组组装为字符串
            }
            return outputtext;//输出
        }
    }
